import org.junit.Test;

import javax.xml.bind.DatatypeConverter;
import java.io.*;

public class BTest {
    String filepath = "target/test-classes/ATest.class";
    String outputFilepath = "target/test-classes/CTest.class";

    @Test
    public void read() throws Exception {
        InputStream is = new FileInputStream(new File(filepath));
        byte[] a = new byte[1];
        while (-1 != is.read(a)) {
            System.out.println(DatatypeConverter.printHexBinary(a).toLowerCase());
        }
    }

    @Test
    public void write() throws Exception {
        String a = "";
        byte[] b = DatatypeConverter.parseHexBinary(a);
        OutputStream os = new FileOutputStream(new File(outputFilepath));
        for (int i = 0; i < b.length; i++) {
            os.write(b[i]);
        }
    }
}
